mapper.select:
select phone, nick_name, wx_name, sum(live_browse) live_browse, sum(special_read) special_read, source_user, sum(live_day) live_day, avg(stay_time) stay_time, sum(read_day) read_day, avg(read_time) read_time, sum(add_browse) add_browse, create_time, sum(live_day_zhao) live_day_zhao, avg(stay_time_zhao) stay_time_zhao, sum(read_day_zhao) read_day_zhao, avg(read_time_zhao) read_time_zhao, sum(add_browse_zhao) add_browse_zhao from bd_master_statis where 1=1
and source_user=#{source_user}
and phone=#{phone}
and nick_name=#{nick_name}
and wx_name=#{wx_name}
and create_time >= DATE_FORMAT(#{start_time},'%Y-%m-%d')
and create_time <= DATE_FORMAT(#{end_time},'%Y-%m-%d')
order by ${sort_field} sort_type
group by phone
LIMIT #{offset}, #{rows}

select count(*) from (select * from bd_master_statis 
where 1=1 

	and source_user=#{source_user}


	and phone=#{phone}


	and nick_name=#{nick_name}


	and wx_name=#{wx_name}


	and create_time >= DATE_FORMAT(#{start_time},'%Y-%m-%d')


	and create_time <= DATE_FORMAT(#{end_time},'%Y-%m-%d')

group by phone) t
